contracts/BondingCurveToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

// Interface for TokenFactory to avoid circular import
interface ILF0GTokenFactory {
    function graduateToken(address token, uint256 usdtAmount, uint256 tokenAmount) external returns (address pairAddress);
    function isTokenCreator(address potentialCreator, address tokenAddress) external view returns (bool);
}

// Minimal interface to interact with the pair for synchronising reserves
interface ILF0GPairSync {
    function sync() external;
}

/**
 * @title BondingCurveToken
 * @dev Token z mechanizmem bonding curve używającym USDT jako waluty bazowej
 */
contract BondingCurveToken is ERC20, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Adres kontraktu USDT
    address public immutable USDT;

    // Stała dla decimals USDT
    uint8 public constant USDT_DECIMALS = 18; // USDT na 0G Galileo Testnet ma 18 decimals

    // Parametry bonding curve
    uint256 public constant CURVE_FACTOR = 1e15; // Współczynnik krzywej
    uint256 public constant PRECISION = 1e18;    // Precyzja dla obliczeń

    // Opłata transakcyjna (0.1%)
    uint256 public constant FEE_RATE = 100;
    uint256 public constant FEE_DENOMINATOR = 100000;

    // Parametry tokenu
    string public description;
    address public factory;

    // Rezerwy tokenów i USDT
    uint256 public usdtReserves;
    uint256 public tokenReserves;
    uint256 public k; // Stały produkt x*y=k

    // Wirtualne rezerwy (do inicjalizacji)
    uint256 public constant VIRTUAL_USDT_RESERVES = 100_000 * 10**USDT_DECIMALS; // 100,000 USDT (18 decimals)
    uint256 public constant VIRTUAL_TOKEN_RESERVES = 1_888_888_888 * 1e18; // ~1.89B tokens

    // Stałe dla progów Gravity Score
    uint256[] public gravityThresholds = [200, 400, 600, 800, 1000];
    uint256[] public unlockPercentages = [500, 1000, 2000, 3000, 3500]; // w setnych częściach procenta

    // Rezerwa dla twórcy (5% całkowitej podaży)
    uint256 public constant CREATOR_RESERVE_PERCENT = 5;
    uint256 public creatorReserve;

    // Aktualny Gravity Score
    uint256 public gravityScore = 0;

    // Mapa osiągniętych progów
    mapping(uint256 => bool) public thresholdReached;

    // Mapa odblokowanych tokenów dla każdego progu
    mapping(uint256 => uint256) public unlockedTokens;

    // Łączna ilość odblokowanych tokenów
    uint256 public totalUnlockedTokens = 0;

    // Skarbiec na opłaty
    address public treasury;

    // Graduation flags and pair address
    bool public isGraduated = false;
    bool public isTradingEnabled = true;
    address public swapPair;

    // Event emitted when the token graduates and liquidity pool is created
    event TokenGraduated(address indexed tokenAddress, address indexed pairAddress, uint256 usdtAmount, uint256 tokenAmount, uint256 timestamp);

    // Zdarzenia
    event TokensPurchased(address indexed buyer, uint256 usdtAmount, uint256 tokenAmount, uint256 feeAmount);
    event TokensSold(address indexed seller, uint256 tokenAmount, uint256 usdtAmount, uint256 feeAmount);
    event GravityScoreUpdated(uint256 oldScore, uint256 newScore);
    event ThresholdReached(uint256 threshold, uint256 tokensUnlocked);
    event FeeCollected(address indexed user, uint256 amount);

    /**
     * @dev Konstruktor tokenów
     * @param _name Nazwa tokenu
     * @param _symbol Symbol tokenu
     * @param _description Opis tokenu
     * @param _creator Adres twórcy tokenu
     * @param _treasury Adres skarbca na opłaty
     * @param _usdt Adres kontraktu USDT
     */
    constructor(
        string memory _name,
        string memory _symbol,
        string memory _description,
        address _creator,
        address _treasury,
        address _usdt
    ) ERC20(_name, _symbol) Ownable() {
        description = _description;
        factory = msg.sender;
        treasury = _treasury;
        USDT = _usdt;

        // Inicjalizacja rezerw i stałej k
        usdtReserves = VIRTUAL_USDT_RESERVES;
        tokenReserves = VIRTUAL_TOKEN_RESERVES;
        k = usdtReserves * tokenReserves;

        // Oblicz rezerwę dla twórcy
        creatorReserve = (VIRTUAL_TOKEN_RESERVES * CREATOR_RESERVE_PERCENT) / 100;

        // Transfer ownership to creator
        _transferOwnership(_creator);
    }

    /**
     * @dev Kupuje tokeny za USDT
     * @param usdtAmount Ilość USDT do wydania
     */
    function buy(uint256 usdtAmount) external nonReentrant {
        require(usdtAmount > 0, "Amount must be > 0");
        require(isTradingEnabled, "Trading disabled after graduation");

        // Obliczenie ilości tokenów do kupienia
        uint256 tokensOut = calculatePurchaseAmount(usdtAmount);
        require(tokensOut > 0, "Zero tokens to mint");

        // Oblicz opłatę 0.1% w USDT
        uint256 fee = (usdtAmount * FEE_RATE) / FEE_DENOMINATOR;
        uint256 totalUsdtAmount = usdtAmount + fee;

        // Aktualizacja rezerw - tylko usdtAmount (bez fee) idzie do rezerwy
        usdtReserves += usdtAmount;
        tokenReserves -= tokensOut;
        k = usdtReserves * tokenReserves;

        // Transfer całkowitej kwoty USDT (usdtAmount + fee) od użytkownika do kontraktu
        IERC20(USDT).safeTransferFrom(msg.sender, address(this), totalUsdtAmount);

        // Transfer opłaty w USDT do skarbca
        if (fee > 0) {
            IERC20(USDT).safeTransfer(treasury, fee);
            emit FeeCollected(msg.sender, fee);
        }

        // Mint tokens dla kupującego
        _mint(msg.sender, tokensOut);

        emit TokensPurchased(msg.sender, usdtAmount, tokensOut, fee);
    }

    /**
     * @dev Sprzedaje tokeny za USDT
     * @param tokenAmount Ilość tokenów do sprzedania
     */
    function sell(uint256 tokenAmount) external nonReentrant {
        require(tokenAmount > 0, "Amount must be > 0");
        require(balanceOf(msg.sender) >= tokenAmount, "Insufficient tokens");
        require(isTradingEnabled, "Trading disabled after graduation");

        // Obliczenie ilości USDT do wypłaty
        uint256 usdtOut = calculateSaleReturn(tokenAmount);
        require(usdtOut > 0, "Zero USDT to return");

        // Oblicz opłatę 0.1% w USDT
        uint256 fee = (usdtOut * FEE_RATE) / FEE_DENOMINATOR;
        uint256 finalUsdtOut = usdtOut - fee;

        // Aktualizacja rezerw
        usdtReserves -= usdtOut;
        tokenReserves += tokenAmount;
        k = usdtReserves * tokenReserves;

        // Burn tokens
        _burn(msg.sender, tokenAmount);

        // Transfer USDT do sprzedającego (po odjęciu opłaty)
        IERC20(USDT).safeTransfer(msg.sender, finalUsdtOut);

        // Transfer opłaty w USDT do skarbca
        if (fee > 0) {
            IERC20(USDT).safeTransfer(treasury, fee);
            emit FeeCollected(msg.sender, fee);
        }

        emit TokensSold(msg.sender, tokenAmount, finalUsdtOut, fee);
    }

    /**
     * @dev Aktualizuje Gravity Score
     * @param _newScore Nowy Gravity Score
     */
    function updateGravityScore(uint256 _newScore) external {
        // Tylko factory lub właściciel może aktualizować score
        require(msg.sender == factory || msg.sender == owner(), "Not authorized");
        require(_newScore <= 1000, "Gravity Score cannot exceed 1000");

        uint256 oldScore = gravityScore;
        gravityScore = _newScore;

        emit GravityScoreUpdated(oldScore, _newScore);

        // Sprawdź progi do odblokowania nagród
        checkThresholds();
    }

    /**
     * @dev Sprawdza progi Gravity Score i odblokowuje nagrody
     */
    function checkThresholds() internal {
        for (uint256 i = 0; i < gravityThresholds.length; i++) {
            uint256 threshold = gravityThresholds[i];

            if (gravityScore >= threshold && !thresholdReached[threshold]) {
                thresholdReached[threshold] = true;

                // Oblicz ilość tokenów do odblokowania
                uint256 tokensToUnlock = creatorReserve * unlockPercentages[i] / 10000;
                unlockedTokens[threshold] = tokensToUnlock;
                totalUnlockedTokens += tokensToUnlock;

                emit ThresholdReached(threshold, tokensToUnlock);
            }
        }
    }

    /**
     * @dev Odbiera odblokowane tokeny
     */
    function claimUnlockedTokens() external nonReentrant {
        // Funkcja może być wywołana bezpośrednio przez twórcę (owner) **lub** pośrednio przez TokenFactory
        // aby ułatwić claim w przypadkach, gdy UI korzysta z metody w fabryce.
        require(
            msg.sender == owner() || msg.sender == factory,
            "Only creator or factory can claim"
        );

        require(totalUnlockedTokens > 0, "No tokens to claim");

        uint256 tokensToTransfer = totalUnlockedTokens;
        totalUnlockedTokens = 0;

        // Zawsze mintujemy tokeny bezpośrednio na adres twórcy (owner()) – niezależnie od tego, kto wywołał funkcję
        _mint(owner(), tokensToTransfer);
    }

    /**
     * @dev Allows the TokenFactory to mint the unlocked tokens directly to any recipient (typically the creator).
     * This bypasses the msg.sender == owner requirement and is useful when the UI triggers the claim via factory.
     * Can be invoked only by `factory` and only if there are unlocked tokens.
     * After minting, the unlocked counter is reset to zero.
     */
    function claimByFactory(address recipient) external nonReentrant {
        require(msg.sender == factory, "Only factory can claim");
        require(totalUnlockedTokens > 0, "No tokens to claim");
        require(recipient != address(0), "Recipient is zero");

        // Verify that recipient is the token creator according to factory records
        require(
            recipient == owner() || ILF0GTokenFactory(factory).isTokenCreator(recipient, address(this)),
            "Recipient is not token creator"
        );

        uint256 amount = totalUnlockedTokens;
        totalUnlockedTokens = 0;

        _mint(recipient, amount);
    }

    /**
     * @dev Oblicza ilość tokenów, które zostaną wybite przy wpłacie określonej ilości USDT
     * @param _usdtAmount Ilość USDT (w jednostkach z 6 decimals)
     * @return Ilość tokenów do wybicia (w jednostkach z 18 decimals)
     */
    function calculatePurchaseAmount(uint256 _usdtAmount) public view returns (uint256) {
        // Formuła: tokeny_do_otrzymania = token_reserves - k/(usdt_reserves + usdt_amount)
        uint256 newUsdtReserves = usdtReserves + _usdtAmount;
        uint256 newTokenReserves = k / newUsdtReserves;

        // Ilość tokenów to różnica
        if (newTokenReserves >= tokenReserves) return 0;
        return tokenReserves - newTokenReserves;
    }

    /**
     * @dev Oblicza ilość USDT, która zostanie zwrócona przy sprzedaży określonej ilości tokenów
     * @param _tokenAmount Ilość tokenów do sprzedania (w jednostkach z 18 decimals)
     * @return Ilość USDT do zwrotu (w jednostkach z 6 decimals)
     */
    function calculateSaleReturn(uint256 _tokenAmount) public view returns (uint256) {
        // Formuła: usdt_do_otrzymania = usdt_reserves - k/(token_reserves + token_amount)
        uint256 newTokenReserves = tokenReserves + _tokenAmount;
        uint256 newUsdtReserves = k / newTokenReserves;

        // Ilość USDT to różnica
        if (newUsdtReserves >= usdtReserves) return 0;
        return usdtReserves - newUsdtReserves;
    }

    /**
     * @dev Zwraca aktualną cenę tokenu (cena za 1 token w USDT)
     * @return Cena za 1 token w USDT
     */
    function getCurrentPrice() public view returns (uint256) {
        // Formuła: price = usdt_reserves / token_reserves
        // Uwzględnienie różnicy w decimals między USDT (6) a tokenem (18)
        return (usdtReserves * 1e18) / tokenReserves;
    }

    /**
     * @dev Zwraca aktualną kapitalizację rynkową (w USDT)
     * @return Kapitalizacja rynkowa
     */
    function getMarketCap() public view returns (uint256) {
        // Precyzja obliczeń - uwzględnia różnicę w decimals
        return (getCurrentPrice() * tokenReserves) / 1e18;
    }

    /**
     * @dev Oblicza opłatę dla transakcji o określonej wartości USDT
     * @param _amount Wartość transakcji w USDT
     * @return Wysokość opłaty w USDT
     */
    function calculateFee(uint256 _amount) public pure returns (uint256) {
        return (_amount * FEE_RATE) / FEE_DENOMINATOR;
    }

    /**
     * @dev Graduates the token – closes bonding-curve trading, adds liquidity to LF0G Swap and registers in the factory/registry.
     * Can be executed only once by the token owner.
     *
     * Requirements:
     * - the caller must be the owner.
     * - the token must not be graduated yet.
     *
     * Workflow:
     * 1. Disable trading via bonding curve.
     * 2. Mint 20 % of total supply to this contract.
     * 3. Approve TokenFactory to pull USDT & freshly-minted tokens.
     * 4. Call TokenFactory.graduateToken() which creates the swap pair, transfers liquidity and registers the token.
     */
    function graduate() external onlyOwner nonReentrant {
        require(!isGraduated, "Token is already graduated");

        // Step 1: mark as graduated & disable BC trading
        isGraduated = true;
        isTradingEnabled = false;

        // Step 2: calculate liquidity amounts
        // Ustal prawdziwą ilość posiadanych USDT (mogą być tylko wirtualne) 
        uint256 contractUsdtBalance = IERC20(USDT).balanceOf(address(this));
        uint256 usdtAmount = contractUsdtBalance;
        // Zmieniamy z 0.1% na 20% całkowitej podaży
        uint256 tokenAmount = (totalSupply() * 20) / 100; // 20% of current supply

        // Mint tokensAmount to this contract so they can be moved into the LP
        _mint(address(this), tokenAmount);

        // Step 3: approve factory for transfers (USDT & token)
        // Ustaw allowance z adresu kontraktu (address(this)) na TokenFactory, aby mogła pobrać tokeny
        _approve(address(this), factory, tokenAmount);

        // Step 4: call the factory bez transferowania USDT - przeprowadzimy to bezpośrednio
        address pairAddress = ILF0GTokenFactory(factory).graduateToken(address(this), 0, tokenAmount);
        swapPair = pairAddress;

        // Step 5: Teraz gdy mamy adres pary, prześlij USDT bezpośrednio
        if (usdtAmount > 0) {
            IERC20(USDT).safeTransfer(pairAddress, usdtAmount);

            // Zaktualizuj rezerwy w kontrakcie pary, aby odzwierciedlić prawdziwe salda
            ILF0GPairSync(pairAddress).sync();
        }

        emit TokenGraduated(address(this), pairAddress, usdtAmount, tokenAmount, block.timestamp);
    }
}

contracts/GraduationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IGraduationRegistry {
    function registerToken(address token, address pair) external;
    function isGraduated(address token) external view returns (bool);
    function getTokenPair(address token) external view returns (address);
    function getGraduatedTokens() external view returns (address[] memory);
}

contract GraduationRegistry is IGraduationRegistry, Ownable {
    // Mapping token -> graduated status
    mapping(address => bool) private _isGraduated;
    // Mapping token -> pair address
    mapping(address => address) private _tokenToPair;
    // Array of graduated tokens for enumeration
    address[] private _graduatedTokens;

    // Authorised registrars (e.g., TokenFactory)
    mapping(address => bool) public authorisedRegistrars;

    event TokenRegistered(address indexed token, address indexed pair);
    event RegistrarStatusChanged(address indexed registrar, bool authorised);

    modifier onlyAuthorised() {
        require(authorisedRegistrars[msg.sender] || msg.sender == owner(), "Not authorised");
        _;
    }

    function addRegistrar(address registrar) external onlyOwner {
        authorisedRegistrars[registrar] = true;
        emit RegistrarStatusChanged(registrar, true);
    }

    function removeRegistrar(address registrar) external onlyOwner {
        authorisedRegistrars[registrar] = false;
        emit RegistrarStatusChanged(registrar, false);
    }

    // IGraduationRegistry implementation
    function registerToken(address token, address pair) external override onlyAuthorised {
        require(!_isGraduated[token], "Already registered");
        require(token != address(0) && pair != address(0), "Zero address");

        _isGraduated[token] = true;
        _tokenToPair[token] = pair;
        _graduatedTokens.push(token);

        emit TokenRegistered(token, pair);
    }

    function isGraduated(address token) external view override returns (bool) {
        return _isGraduated[token];
    }

    function getTokenPair(address token) external view override returns (address) {
        return _tokenToPair[token];
    }

    function getGraduatedTokens() external view override returns (address[] memory) {
        return _graduatedTokens;
    }
}

contracts/GravityScoreSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./TokenFactory.sol";

/**
 * @title GravityScoreSystem
 * @dev System obliczania i przechowywania Gravity Score
 */
contract GravityScoreSystem is Ownable {
    // Adres fabryki tokenów
    address public tokenFactory;
    
    // Mapowanie adresów tokenów do ich Gravity Score
    mapping(address => uint256) public tokenScores;
    
    // Mapowanie adresów tokenów do timestampów ostatniej aktualizacji
    mapping(address => uint256) public lastScoreUpdate;
    
    // Zdarzenia
    event ScoreUpdated(address indexed tokenAddress, uint256 newScore, uint256 timestamp);
    
    /**
     * @dev Konstruktor ustawiający właściciela
     */
    constructor() Ownable() {}
    
    /**
     * @dev Ustawia adres fabryki tokenów
     * @param _tokenFactory Adres fabryki
     */
    function setTokenFactory(address _tokenFactory) external onlyOwner {
        tokenFactory = _tokenFactory;
    }
    
    /**
     * @dev Aktualizuje Gravity Score dla tokenu
     * @param _tokenAddress Adres tokenu
     * @param _newScore Nowy Gravity Score
     */
    function updateScore(address _tokenAddress, uint256 _newScore) external onlyOwner {
        require(_newScore <= 1000, "Gravity Score cannot exceed 1000");
        
        tokenScores[_tokenAddress] = _newScore;
        lastScoreUpdate[_tokenAddress] = block.timestamp;
        
        // Aktualizuj score w fabryce tokenów
        TokenFactory(tokenFactory).updateGravityScore(_tokenAddress, _newScore);
        
        emit ScoreUpdated(_tokenAddress, _newScore, block.timestamp);
    }
    
    /**
     * @dev Aktualizuje Gravity Score dla wielu tokenów naraz
     * @param _tokenAddresses Tablica adresów tokenów
     * @param _newScores Tablica nowych Gravity Score
     */
    function batchUpdateScores(address[] calldata _tokenAddresses, uint256[] calldata _newScores) external onlyOwner {
        require(_tokenAddresses.length == _newScores.length, "Arrays length mismatch");
        
        for (uint256 i = 0; i < _tokenAddresses.length; i++) {
            require(_newScores[i] <= 1000, "Gravity Score cannot exceed 1000");
            
            tokenScores[_tokenAddresses[i]] = _newScores[i];
            lastScoreUpdate[_tokenAddresses[i]] = block.timestamp;
            
            // Aktualizuj score w fabryce tokenów
            TokenFactory(tokenFactory).updateGravityScore(_tokenAddresses[i], _newScores[i]);
            
            emit ScoreUpdated(_tokenAddresses[i], _newScores[i], block.timestamp);
        }
    }
    
    /**
     * @dev Pobiera Gravity Score dla tokenu
     * @param _tokenAddress Adres tokenu
     * @return Gravity Score
     */
    function getScore(address _tokenAddress) external view returns (uint256) {
        return tokenScores[_tokenAddress];
    }
    
    /**
     * @dev Pobiera timestamp ostatniej aktualizacji Gravity Score dla tokenu
     * @param _tokenAddress Adres tokenu
     * @return Timestamp ostatniej aktualizacji
     */
    function getLastUpdateTime(address _tokenAddress) external view returns (uint256) {
        return lastScoreUpdate[_tokenAddress];
    }
}

contracts/LF0GSwapFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./LF0GSwapPair.sol";

contract LF0GSwapFactory is Ownable {
    // Mapping from tokenA->tokenB to pair
    mapping(address => mapping(address => address)) public getPair;
    address[] public allPairs;

    event PairCreated(address indexed token0, address indexed token1, address pair, uint indexed pairIndex);

    function allPairsLength() external view returns (uint) {
        return allPairs.length;
    }

    /**
     * @dev Creates a pair for tokenA & tokenB. Reverts if it already exists.
     */
    function createPair(address tokenA, address tokenB) external returns (address pair) {
        require(tokenA != tokenB, "LF0G: IDENTICAL_ADDRESSES");
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "LF0G: ZERO_ADDRESS");
        require(getPair[token0][token1] == address(0), "LF0G: PAIR_EXISTS");

        bytes memory bytecode = type(LF0GSwapPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        LF0GSwapPair(pair).setup(token0, token1);

        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair;
        allPairs.push(pair);

        emit PairCreated(token0, token1, pair, allPairs.length - 1);
    }
}

contracts/LF0GSwapPair.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract LF0GSwapPair is ERC20 {
    using SafeERC20 for IERC20;

    address public token0;
    address public token1;
    address public factory;

    uint112 private reserve0; // token0 reserve
    uint112 private reserve1; // token1 reserve
    uint32  private blockTimestampLast;

    bool public initialised;

    modifier onlyFactory() {
        require(msg.sender == factory, "LF0G: FORBIDDEN");
        _;
    }

    constructor() ERC20("LF0G LP", "LF0G-LP") {
        factory = msg.sender;
    }

    function setup(address _token0, address _token1) external onlyFactory {
        require(!initialised, "Already setup");
        token0 = _token0;
        token1 = _token1;
        initialised = true;
    }

    // Called once by TokenFactory after liquidity is transferred
    function initialize(uint tokenAmount, uint usdtAmount) external {
        _update(uint112(tokenAmount), uint112(usdtAmount));
        // Mint LP tokens to burn address (0xdead) making liquidity permanent
        _mint(address(0xdead), Math.sqrt(tokenAmount * usdtAmount));
    }

    function _update(uint112 _reserve0, uint112 _reserve1) private {
        reserve0 = _reserve0;
        reserve1 = _reserve1;
        blockTimestampLast = uint32(block.timestamp);
    }

    // Basic swap (X * Y = K, no fees, simple demo). Assumes token0 is the custom token, token1 is USDT.
    function swap(uint amount0Out, uint amount1Out, address to) external {
        require(amount0Out > 0 || amount1Out > 0, "LF0G: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        require(amount0Out < _reserve0 && amount1Out < _reserve1, "LF0G: INSUFFICIENT_LIQUIDITY");

        if (amount0Out > 0) IERC20(token0).safeTransfer(to, amount0Out);
        if (amount1Out > 0) IERC20(token1).safeTransfer(to, amount1Out);

        uint balance0 = IERC20(token0).balanceOf(address(this));
        uint balance1 = IERC20(token1).balanceOf(address(this));

        uint amount0In = balance0 > (_reserve0 - amount0Out) ? balance0 - (_reserve0 - amount0Out) : 0;
        uint amount1In = balance1 > (_reserve1 - amount1Out) ? balance1 - (_reserve1 - amount1Out) : 0;
        require(amount0In > 0 || amount1In > 0, "LF0G: INSUFFICIENT_INPUT");

        // Simple invariant check without fees
        require(balance0 * balance1 >= uint(_reserve0) * uint(_reserve1), "LF0G: K");

        _update(uint112(balance0), uint112(balance1));
    }

    function getReserves() public view returns (uint112, uint112, uint32) {
        return (reserve0, reserve1, blockTimestampLast);
    }

    /**
     * @dev Executes a swap of exact tokens for tokens in one transaction.
     * This combines the transfer and swap operations, simplifying the UX.
     * @param amountIn The exact amount of input tokens to send
     * @param amountOutMin The minimum amount of output tokens to receive
     * @param isToken0 Whether the input token is token0 (true) or token1 (false)
     * @param to The recipient address to receive the output tokens
     * @return amountOut The actual amount of output tokens received
     */
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        bool isToken0,
        address to
    ) external returns (uint amountOut) {
        require(amountIn > 0, "LF0G: INSUFFICIENT_INPUT_AMOUNT");
        
        // Get current reserves
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        
        // Determine which token is input/output based on isToken0
        uint reserveIn = isToken0 ? _reserve0 : _reserve1;
        uint reserveOut = isToken0 ? _reserve1 : _reserve0;
        address tokenIn = isToken0 ? token0 : token1;
        address tokenOut = isToken0 ? token1 : token0;
        
        // Calculate output amount using x*y=k formula
        // amountOut = (reserveOut * amountIn) / (reserveIn + amountIn)
        amountOut = (reserveOut * amountIn) / (reserveIn + amountIn);
        require(amountOut >= amountOutMin, "LF0G: INSUFFICIENT_OUTPUT_AMOUNT");
        
        // Transfer input tokens from sender to pair
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);
        
        // Transfer output tokens to recipient
        IERC20(tokenOut).safeTransfer(to, amountOut);
        
        // Update reserves
        uint balance0 = IERC20(token0).balanceOf(address(this));
        uint balance1 = IERC20(token1).balanceOf(address(this));
        
        // Verify k after swap
        require(balance0 * balance1 >= uint(_reserve0) * uint(_reserve1), "LF0G: K");
        
        // Update internal reserves
        _update(uint112(balance0), uint112(balance1));
        
        return amountOut;
    }

    /**
     * @dev Calculates the output amount for a given input amount based on reserves
     * @param amountIn The input amount 
     * @param isToken0In Whether the input token is token0 (true) or token1 (false)
     * @return Amount of output tokens that would be received
     */
    function getAmountOut(uint amountIn, bool isToken0In) public view returns (uint) {
        require(amountIn > 0, "LF0G: INSUFFICIENT_INPUT_AMOUNT");
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        
        uint reserveIn = isToken0In ? _reserve0 : _reserve1;
        uint reserveOut = isToken0In ? _reserve1 : _reserve0;
        
        return (reserveOut * amountIn) / (reserveIn + amountIn);
    }

    /**
     * @dev Calculates the input amount needed for a given output amount based on reserves
     * @param amountOut The desired output amount 
     * @param isToken0In Whether the input token is token0 (true) or token1 (false)
     * @return Amount of input tokens that would be required
     */
    function getAmountIn(uint amountOut, bool isToken0In) public view returns (uint) {
        require(amountOut > 0, "LF0G: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        
        uint reserveIn = isToken0In ? _reserve0 : _reserve1;
        uint reserveOut = isToken0In ? _reserve1 : _reserve0;
        
        require(amountOut < reserveOut, "LF0G: INSUFFICIENT_LIQUIDITY");
        
        // amountIn = (reserveIn * amountOut) / (reserveOut - amountOut)
        return (reserveIn * amountOut) / (reserveOut - amountOut);
    }

    /**
     * @dev Provides a price quote for the given amount and reserves
     * @param amountIn The input amount
     * @param reserveIn The reserve of the input token
     * @param reserveOut The reserve of the output token
     * @return Amount of output tokens for the given input and reserves
     */
    function quote(uint amountIn, uint reserveIn, uint reserveOut) public pure returns (uint) {
        require(amountIn > 0, "LF0G: INSUFFICIENT_INPUT_AMOUNT");
        require(reserveIn > 0 && reserveOut > 0, "LF0G: INSUFFICIENT_LIQUIDITY");
        
        return (amountIn * reserveOut) / reserveIn;
    }

    /**
     * @dev Executes a swap to get exact tokens for input tokens.
     * This function allows specifying the exact amount of output tokens desired.
     * @param amountOut The exact amount of output tokens to receive
     * @param amountInMax The maximum amount of input tokens willing to spend
     * @param isToken0 Whether the input token is token0 (true) or token1 (false)
     * @param to The recipient address to receive the output tokens
     * @return amountIn The actual amount of input tokens spent
     */
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        bool isToken0,
        address to
    ) external returns (uint amountIn) {
        require(amountOut > 0, "LF0G: INSUFFICIENT_OUTPUT_AMOUNT");
        
        // Get current reserves
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        
        // Determine which token is input/output based on isToken0
        uint reserveIn = isToken0 ? _reserve0 : _reserve1;
        uint reserveOut = isToken0 ? _reserve1 : _reserve0;
        address tokenIn = isToken0 ? token0 : token1;
        address tokenOut = isToken0 ? token1 : token0;
        
        // Ensure output amount is available
        require(amountOut < reserveOut, "LF0G: INSUFFICIENT_LIQUIDITY");
        
        // Calculate input amount required using x*y=k formula
        // amountIn = (reserveIn * amountOut) / (reserveOut - amountOut)
        amountIn = (reserveIn * amountOut) / (reserveOut - amountOut);
        require(amountIn <= amountInMax, "LF0G: EXCESSIVE_INPUT_AMOUNT");
        
        // Transfer input tokens from sender to pair
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);
        
        // Transfer output tokens to recipient
        IERC20(tokenOut).safeTransfer(to, amountOut);
        
        // Update reserves
        uint balance0 = IERC20(token0).balanceOf(address(this));
        uint balance1 = IERC20(token1).balanceOf(address(this));
        
        // Verify k after swap
        require(balance0 * balance1 >= uint(_reserve0) * uint(_reserve1), "LF0G: K");
        
        // Update internal reserves
        _update(uint112(balance0), uint112(balance1));
        
        return amountIn;
    }

    /**
     * @dev Force updates the stored reserves to match the actual balances in the pair.
     *      This is helpful when liquidity is transferred directly (outside of swap/add routines),
     *      e.g. during the graduation procedure.
     */
    function sync() external {
        uint112 balance0 = uint112(IERC20(token0).balanceOf(address(this)));
        uint112 balance1 = uint112(IERC20(token1).balanceOf(address(this)));
        _update(balance0, balance1);
    }
}

contracts/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./BondingCurveToken.sol";

// Interface declarations to avoid circular dependencies
interface ILF0GSwapFactory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

interface ILF0GSwapPair {
    function initialize(uint tokenAmount, uint usdtAmount) external;
}

interface IGraduationRegistry {
    function registerToken(address token, address pair) external;
}

/**
 * @title TokenFactory
 * @dev Fabryka do tworzenia tokenów z bonding curve
 */
contract TokenFactory is Ownable {
    using SafeERC20 for IERC20;

    // Lista utworzonych tokenów
    address[] public createdTokens;

    // Mapowanie twórcy do jego tokenów
    mapping(address => address[]) public creatorTokens;

    // Mapping adresu tokenu do jego danych
    mapping(address => TokenData) public tokenData;

    // Adres systemu Gravity Score
    address public gravityScoreSystem;

    // Adres USDT
    address public immutable USDT;

    // Skarbiec na opłaty
    address public treasury;

    // Addresses of the SwapFactory and GraduationRegistry (set once by owner)
    address public swapFactory;
    address public graduationRegistry;

    // Graduation tracking
    mapping(address => bool) public isTokenGraduated;
    mapping(address => address) public tokenToPair;
    address[] public graduatedTokens;

    // Event emitted when a token graduates
    event TokenGraduated(address indexed token, address indexed pair);

    // Struktura danych tokenu
    struct TokenData {
        string name;
        string symbol;
        string description;
        address creator;
        uint256 creationTime;
        uint256 gravityScore;
    }

    // Zdarzenia
    event TokenCreated(address indexed tokenAddress, string name, string symbol, address indexed creator);

    /**
     * @dev Konstruktor z adresem skarbca
     * @param _treasury Adres skarbca
     * @param _gravityScoreSystem Adres systemu Gravity Score
     * @param _usdt Adres kontraktu USDT
     */
    constructor(address _treasury, address _gravityScoreSystem, address _usdt) Ownable() {
        treasury = _treasury;
        gravityScoreSystem = _gravityScoreSystem;
        USDT = _usdt;
    }

    /**
     * @dev Tworzy nowy token
     * @param _name Nazwa tokenu
     * @param _symbol Symbol tokenu
     * @param _description Opis tokenu
     */
    function createToken(
        string memory _name,
        string memory _symbol,
        string memory _description
    ) external {
        // Tworzymy nowy token
        BondingCurveToken newToken = new BondingCurveToken(
            _name,
            _symbol,
            _description,
            msg.sender,
            treasury,
            USDT
        );

        // Zapisujemy adres tokenu
        address tokenAddress = address(newToken);
        createdTokens.push(tokenAddress);
        creatorTokens[msg.sender].push(tokenAddress);

        // Zapisujemy dane tokenu
        tokenData[tokenAddress] = TokenData({
            name: _name,
            symbol: _symbol,
            description: _description,
            creator: msg.sender,
            creationTime: block.timestamp,
            gravityScore: 0
        });

        emit TokenCreated(tokenAddress, _name, _symbol, msg.sender);
    }

    /**
     * @dev Aktualizuje Gravity Score dla tokenu
     * @param _tokenAddress Adres tokenu
     * @param _newScore Nowy Gravity Score
     */
    function updateGravityScore(address _tokenAddress, uint256 _newScore) external {
        require(msg.sender == gravityScoreSystem || msg.sender == owner(), "Not authorized");

        // Aktualizuj score w danych tokenu
        tokenData[_tokenAddress].gravityScore = _newScore;

        // Aktualizuj score w kontrakcie tokenu
        BondingCurveToken(_tokenAddress).updateGravityScore(_newScore);
    }

    /**
     * @dev Pobiera tokeny utworzone przez określonego twórcę
     * @param _creator Adres twórcy
     * @return Lista adresów tokenów
     */
    function getCreatorTokens(address _creator) external view returns (address[] memory) {
        return creatorTokens[_creator];
    }

    /**
     * @dev Pobiera wszystkie utworzone tokeny
     * @return Lista adresów tokenów
     */
    function getAllTokens() external view returns (address[] memory) {
        return createdTokens;
    }

    /**
     * @dev Aktualizuje adres skarbca
     * @param _newTreasury Nowy adres skarbca
     */
    function updateTreasury(address _newTreasury) external onlyOwner {
        treasury = _newTreasury;
    }

    /**
     * @dev Aktualizuje adres systemu Gravity Score
     * @param _newSystem Nowy adres systemu
     */
    function updateGravityScoreSystem(address _newSystem) external onlyOwner {
        gravityScoreSystem = _newSystem;
    }

    /**
     * @dev Sets addresses for SwapFactory and GraduationRegistry. Callable only once per variable.
     */
    function setGraduationAddresses(address _swapFactory, address _graduationRegistry) external onlyOwner {
        require(_swapFactory != address(0) && _graduationRegistry != address(0), "Zero address");
        swapFactory = _swapFactory;
        graduationRegistry = _graduationRegistry;
    }

    /**
     * @dev Finalises token graduation. Must be called by the token contract itself after it approved allowances.
     * Transfers liquidity to the newly-created pair, initialises it and registers the graduation.
     * @return pair Address of the newly created liquidity pair.
     */
    function graduateToken(address token, uint256 usdtAmount, uint256 tokenAmount) external returns (address pair) {
        require(msg.sender == token, "Caller must be token");
        require(!isTokenGraduated[token], "Token already graduated");
        require(swapFactory != address(0), "SwapFactory not set");

        // 1. Create pair (token, USDT)
        pair = ILF0GSwapFactory(swapFactory).createPair(token, USDT);

        // 2. Pull liquidity from token contract to pair
        // USDT będzie przesłany bezpośrednio przez kontrakt tokenu (zamiast transferFrom)
        IERC20(token).safeTransferFrom(token, pair, tokenAmount);

        // 3. Initialise pair reserves (jeśli brak USDT to ustaw 1 wei aby uniknąć /0)
        uint256 initUsdt = usdtAmount == 0 ? 1 : usdtAmount;
        ILF0GSwapPair(pair).initialize(tokenAmount, initUsdt);

        // 4. Update mappings
        isTokenGraduated[token] = true;
        tokenToPair[token] = pair;
        graduatedTokens.push(token);

        // 5. Register in external registry (optional)
        if (graduationRegistry != address(0)) {
            IGraduationRegistry(graduationRegistry).registerToken(token, pair);
        }

        emit TokenGraduated(token, pair);
    }

    /**
     * @dev Returns list of graduated tokens.
     */
    function getGraduatedTokens() external view returns (address[] memory) {
        return graduatedTokens;
    }

    /**
     * @dev Returns swap pair for given token.
     */
    function getSwapPair(address token) external view returns (address) {
        return tokenToPair[token];
    }

    /**
     * @dev Allows a token creator to claim their unlocked tokens through the factory.
     * This is useful when the front-end interacts tylko with the factory contract. The factory
     * verifies that the caller is the owner (creator) of the given token, then forwards the
     * claim call. Inside the token, the modified `claimByFactory()` now accepts calls
     * originating from the factory.
     *
     * Requirements:
     * - Caller must be the `owner()` of the token (creator) or be registered as a creator in creatorTokens mapping.
     * - The token must have unlocked tokens available.
     */
    function claimCreatorTokens(address tokenAddress) external {
        BondingCurveToken token = BondingCurveToken(tokenAddress);

        // Sprawdź, czy wywołujący jest właścicielem tokenu LUB jest twórcą w mapowaniu
        bool isOwner = token.owner() == msg.sender;
        bool isCreator = isTokenCreator(msg.sender, tokenAddress);

        require(isOwner || isCreator, "Caller is not token owner or creator");

        // Wywołaj claimByFactory – token zresetuje licznik i wyśle je bezpośrednio do właściciela
        token.claimByFactory(msg.sender);
    }

    /**
     * @dev Sprawdza, czy podany adres jest twórcą tokenu według zapisów w kontrakcie
     * @param potentialCreator Adres do sprawdzenia
     * @param tokenAddress Adres tokenu
     * @return true jeśli podany adres jest twórcą tokenu
     */
    function isTokenCreator(address potentialCreator, address tokenAddress) public view returns (bool) {
        // Sprawdź dane w tokenData
        if (tokenData[tokenAddress].creator == potentialCreator) return true;

        // Sprawdź w mapowaniu creatorTokens
        address[] memory tokens = creatorTokens[potentialCreator];
        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == tokenAddress) return true;
        }

        return false;
    }
}

